#!/usr/bin/env python3

theInput = """



"""

keyboardTable = [[1, 2, 3], [4, 5, 6], [7, 8, 9]]

x, y = 1, 1 # position of 5
codeList = []
instrList = theInput.split('\n')
for instructionString in instrList:
    for uinstruction in instructionString:
        if uinstruction == 'U':
            y -= 1
        if uinstruction == 'D':
            y += 1
        if uinstruction == 'L':
            x -= 1
        if uinstruction == 'R':
            x += 1
        x = min(max(x, 0), 2)
        y = min(max(y, 0), 2)
    codeList.append(keyboardTable[y][x])
print("part 1: ", end="")
print(codeList)

